	limit #{start}, #{limit}

select
count(sid)
from pcm_shop_brand_relation


		and sid = #{sid,jdbcType=BIGINT}


		and shop_sid = #{shopSid,jdbcType=VARCHAR}


		and brand_sid = #{brandSid,jdbcType=VARCHAR}


		and brand_group_code = #{brandGroupCode,jdbcType=VARCHAR}


		and brand_name = #{brandName,jdbcType=VARCHAR}


		and status = #{status,jdbcType=INTEGER}

SELECT pb.sid AS brandSid, pb.brand_sid AS brandCode, pb.brand_name AS brandName, po.sid AS shopSid, po.organization_code AS shopCode, po.organization_name AS shopName FROM pcm_brand pb INNER JOIN pcm_organization po ON pb.shop_type = po.store_type
po.organization_status = 0 AND po.organization_type = 3 AND pb.brand_type = 1
AND pb.status = 0
AND pb.brand_sid = #{brandCode}
AND po.organization_code = #{shopCode}

SELECT pb.sid AS brandSid, pb.brand_name AS brandName, po.sid AS shopSid FROM pcm_brand pb LEFT JOIN pcm_organization po ON pb.shop_type = po.store_type
po.organization_status = 0 AND po.organization_type = 3 AND pb.status = 0 AND pb.brand_type = 1
AND pb.brand_sid = #{brandSid}
AND po.organization_code = #{shopCode}

SELECT DISTINCT pb.sid AS sid, pb.brand_sid AS brandSid, pb.brand_name AS brandName,po.sid AS shopSid FROM pcm_brand pb INNER JOIN pcm_shop_brand_relation psb ON pb.sid = psb.brand_sid INNER JOIN ( SELECT * FROM pcm_organization WHERE organization_type = 3 AND organization_status = 0
AND organization_code = #{shopCode}
) po ON psb.shop_sid = po.sid
pb.status = 0 AND psb.status = 0 AND pb.brand_type = 1
AND pb.parent_sid = #{parentSid,jdbcType=BIGINT}
AND pb.brand_sid = #{brandSid}
AND psb.shop_sid = #{shopSid,jdbcType=VARCHAR}
