SELECT pcpd.* from pcm_category_props_dict AS pcpd INNER JOIN pcm_category_prop_values AS pcpv ON pcpd.props_sid=pcpv.props_sid INNER JOIN pcm_category as pc ON pcpv.category_sid=pc.category_sid where pc.category_sid in (categorySids)
and pc.category_sid in (categorySids)
and category_name = #{categoryName,jdbcType=VARCHAR}
and props_sid = #{propsSid,jdbcType=BIGINT}
and props_name = #{propsName,jdbcType=VARCHAR}
and channel_sid = #{channelSid,jdbcType=BIGINT}
group by props_sid

