SELECT sid AS sid, contract_code AS contractCode, store_code AS storeCode, supply_code AS supplyCode, manage_type AS manageType, buy_type AS buyType, oper_mode AS operMode, business_type AS businessType, input_tax AS inputTax, output_tax AS outputTax, commission_rate AS commissionRate, col1 AS col1, col2 AS col2, col3 AS col3 FROM pcm_contract_log
and sid = #{sid}
and contract_code = #{contractCode,jdbcType=VARCHAR}
and store_code = #{storeCode,jdbcType=VARCHAR}
and supply_code = #{supplyCode,jdbcType=VARCHAR}
and manage_type = #{manageType}
ORDER BY CONVERT (contract_code USING gbk)
limit #{start}, #{limit}

SELECT
COUNT(*)
FROM
pcm_contract_log


        and sid = #{sid}


        and contract_code = #{contractCode,jdbcType=VARCHAR}


        and store_code = #{storeCode,jdbcType=VARCHAR}


        and supply_code = #{supplyCode,jdbcType=VARCHAR}


