SELECT clog.contract_code AS contractCode, clog.supply_code AS supplyCode, sup.supply_name AS supplyName, clog.store_code AS storeCode, org.organization_name AS storeName, clog.business_type AS businessType, clog.manage_type AS manageType, clog.buy_type AS buyType, clog.oper_mode AS operMode, IFNULL(clog.input_tax,"") AS inputTax, IFNULL(clog.output_tax,"") AS outputTax, IFNULL(clog.commission_rate,"") AS commissionRate, IFNULL(clog.col1,"") AS categoryCode, IFNULL(cate.`name`,"") AS categoryName FROM (SELECT * from pcm_contract_log
and supply_code in
#{supplyCode,jdbcType=VARCHAR}
AND contract_code = #{contractCode,jdbcType=VARCHAR}
AND manage_type = #{manageType,jdbcType=BIGINT}
AND store_code = #{storeCode,jdbcType=VARCHAR}
) clog INNER JOIN pcm_supply_info sup ON sup.supply_code = clog.supply_code
AND sup.supply_name like '%${supplyName}%'
INNER JOIN pcm_organization org ON org.organization_code = clog.store_code LEFT JOIN pcm_category cate ON cate.category_code = clog.col1 AND cate.`status` = 0 AND cate.is_display = 1 AND cate.category_type = 3
limit #{start}, #{limit}

