AND sup.supply_name like '%${supplyName}%'
INNER JOIN pcm_organization org ON org.organization_code = clog.store_code LEFT JOIN pcm_category cate ON cate.category_code = clog.col1 AND cate.`status` = 0 AND cate.is_display = 1 AND cate.category_type = 3

