mapper.select:
SELECT edi.* FROM pcm_shoppe_product AS pro JOIN pcm_shoppe AS shoppe ON shoppe.sid = pro.shoppe_sid JOIN pcm_channel_sale_config AS sale ON sale.shoppe_pro_sid = shoppe.sid AND sale.sale_stauts = 0 JOIN pcm_channel AS channel ON sale.channel_sid = channel.channel_code AND channel.`status` = 0 JOIN pcm_shoppe_product_edi_relation AS edi ON channel.channel_code = edi.channel_code AND edi.field1 = pro.shoppe_pro_sid AND edi.ifdel = 1
and pro.shoppe_pro_sid = #{shoppeProCode,jdbcType=VARCHAR}
and channel.channel_code = #{channelCode,jdbcType=VARCHAR}

select psper.sid, psper.shoppe_pro_sid, psper.channel_pro_sid, psper.channel_code, psper.is_presell, psper.is_pay_reducestock, ps.pro_sum, ps.stock_type_sid from pcm_shoppe_product_edi_relation psper INNER JOIN pcm_stock ps ON psper.field1 = ps.shoppe_pro_sid
and psper.field1= #{shoppeProSid,jdbcType=VARCHAR}
and psper.channel_code = #{channelCode,jdbcType=VARCHAR}
and ps.channel_sid = #{stockChannelCode,jdbcType=VARCHAR}
and ps.stock_type_sid = #{stockTypeSid,jdbcType=INTEGER}
and ps.stock_type_sid =1001
and psper.ifdel = 1

select psper.sid, psper.shoppe_pro_sid, psper.channel_pro_sid, psper.channel_code, psper.is_presell, psper.is_pay_reducestock, ps.pro_sum, ps.stock_type_sid from pcm_shoppe_product_edi_relation psper INNER JOIN pcm_stock ps ON psper.shoppe_pro_sid = ps.shoppe_pro_sid AND psper.channel_code = ps.channel_sid
#{proSid}
and channel_code = #{channelCode,jdbcType=VARCHAR}
and ps.stock_type_sid = #{stockTypeSid,jdbcType=INTEGER}
and ps.stock_type_sid =1001
and psper.ifdel = 1

select psper.sid, psper.shoppe_pro_sid, psper.channel_pro_sid, psper.channel_code, psper.is_presell, psper.is_pay_reducestock, field1 from pcm_shoppe_product_edi_relation psper
and psper.field1= #{shoppeProSid,jdbcType=VARCHAR}
and psper.channel_code = #{channelCode,jdbcType=VARCHAR}
and psper.ifdel = 1

