mapper.select:
SELECT channel_code AS code, channel_name AS name, opt_user AS actionPerson FROM pcm_channel
and sid = #{sid,jdbcType=BIGINT}
and status = #{status,jdbcType=INTEGER}

select

from pcm_channel


        and sid = #{sid,jdbcType=BIGINT}


        and channel_code = #{channelCode,jdbcType=VARCHAR}


        and channel_name = #{channelName,jdbcType=VARCHAR}


        and status = #{status,jdbcType=INTEGER}


        and opt_user = #{optUser,jdbcType=VARCHAR}


        and opt_date = #{optDate,jdbcType=TIMESTAMP}

select

from pcm_channel


        and sid = #{sid,jdbcType=BIGINT}


        and channel_code = #{channelCode,jdbcType=VARCHAR}


        and channel_name = #{channelName,jdbcType=VARCHAR}


        and status = #{status,jdbcType=INTEGER}


        and opt_user = #{optUser,jdbcType=VARCHAR}


        and opt_date = #{optDate,jdbcType=TIMESTAMP}


order by
sid desc


        limit
        #{start}, #{limit}

select
count(*)
from pcm_channel


        and sid = #{sid,jdbcType=BIGINT}


        and channel_code = #{channelCode,jdbcType=VARCHAR}


        and channel_name = #{channelName,jdbcType=VARCHAR}


        and status = #{status,jdbcType=INTEGER}


        and opt_user = #{optUser,jdbcType=VARCHAR}


        and opt_date = #{optDate,jdbcType=TIMESTAMP}

select

from pcm_channel

    status=0

        sid = #{sid,jdbcType=BIGINT}


        and channel_code = #{channelCode,jdbcType=VARCHAR}


        and channel_name = #{channelName,jdbcType=VARCHAR}


        and status = #{status,jdbcType=BIGINT}


        and opt_user = #{optUser,jdbcType=VARCHAR}


        and opt_date = #{optDate,jdbcType=TIMESTAMP}


order by sid asc

select

from pcm_channel


        and sid = #{sid,jdbcType=BIGINT}


        and channel_code = #{channelCode,jdbcType=VARCHAR}


        and channel_name like
        "%"#{channelName,jdbcType=VARCHAR}"%"


        and status = #{status,jdbcType=INTEGER}


        and opt_user = #{optUser,jdbcType=VARCHAR}


        and opt_date = #{optDate,jdbcType=TIMESTAMP}



    order by
    sid desc


    ORDER BY
    CONVERT(channel_name USING gbk)


    limit #{start}, #{limit}

