mapper.select:
SELECT pcpr.store_code,pcpr.matnr,pcpr.zsprice,pcpr.bdate,pcpr.change_code from pcm_change_price_record pcpr INNER JOIN( SELECT store_code,matnr,bdate,MAX(change_code) as change_code FROM pcm_change_price_record WHERE attribute5 = '1' and DATE_FORMAT(NOW(),'%Y-%m-%d')=DATE_FORMAT(bdate,'%Y-%m-%d') GROUP BY store_code,matnr,bdate
limit #{start}, #{limit}
) pcpr2 on pcpr.store_code=pcpr2.store_code and pcpr.matnr=pcpr2.matnr AND pcpr.bdate=pcpr2.bdate AND pcpr.change_code=pcpr2.change_code

select
from pcm_change_price_record WHERE datediff( date_format(now(), '%Y-%c-%d %h:%i:%s'), date_format(bdate, '%Y-%c-%d %h:%i:%s') ) >= 15
limit #{start}, #{limit}
