mapper.select:
select

from pcm_stock_change_record

	1=1

		and bills_no = #{billsNo,jdbcType=VARCHAR}


		and shoppe_pro_sid = #{shoppeProSid,jdbcType=VARCHAR}


		and stock_sid = #{stockSid,jdbcType=BIGINT}


		and change_sum = #{changeSum,jdbcType=BIGINT}


		and change_type_sid = #{changeTypeSid,jdbcType=INTEGER}


		and change_time = #{changeTime,jdbcType=TIMESTAMP}


		and note = #{note,jdbcType=VARCHAR}

select

from pcm_stock_change_record


		and bills_no = #{billsNo,jdbcType=VARCHAR}


		and shoppe_pro_sid = #{shoppeProSid,jdbcType=VARCHAR}


		and stock_sid = #{stockSid,jdbcType=BIGINT}


		and change_sum = #{changeSum,jdbcType=BIGINT}


		and change_type_sid = #{changeTypeSid,jdbcType=INTEGER}


		and change_time = #{changeTime,jdbcType=TIMESTAMP}


		and note = #{note,jdbcType=VARCHAR}


order by
sid asc
limit
#{start}, #{limit}

select
count(*)
from pcm_stock_change_record


		and bills_no = #{billsNo,jdbcType=VARCHAR}


		and shoppe_pro_sid = #{shoppeProSid,jdbcType=VARCHAR}


		and stock_sid = #{stockSid,jdbcType=BIGINT}


		and change_sum = #{changeSum,jdbcType=BIGINT}


		and change_type_sid = #{changeTypeSid,jdbcType=INTEGER}


		and change_time = #{changeTime,jdbcType=TIMESTAMP}


		and note = #{note,jdbcType=VARCHAR}

SELECT pscr.bills_no, pscr.shoppe_pro_sid, change_sum , pscr.pro_sum, note, change_type_sid, ps.channel_sid, pc.channel_name, DATE_FORMAT(pscr.change_time,'%Y-%m-%d %H:%i:%S') as change_time FROM pcm_stock_change_record pscr INNER JOIN pcm_stock ps ON ps.sid = pscr.stock_sid INNER JOIN pcm_channel pc on pc.channel_code=ps.channel_sid
and pscr.shoppe_pro_sid = #{shoppeProSid,jdbcType=VARCHAR}
AND change_type_sid IN ( '1001', '1021','1022', '1023', '1012', '1013')
AND change_type_sid=#{changeTypeSid,jdbcType=VARCHAR}
and channel_sid = #{channelSid,jdbcType=VARCHAR}
and channel_sid = '0'
ORDER BY change_time, pscr.sid
limit #{start}, #{limit}

SELECT count(1) FROM pcm_stock_change_record pscr INNER JOIN pcm_stock ps ON ps.sid = pscr.stock_sid INNER JOIN pcm_channel pc on pc.channel_code=ps.channel_sid
and pscr.shoppe_pro_sid = #{shoppeProSid,jdbcType=VARCHAR}
AND change_type_sid IN ( '1001','1021','1022', '1023', '1012', '1013')
AND change_type_sid=#{changeTypeSid,jdbcType=VARCHAR}
and channel_sid = #{channelSid}
and channel_sid = '0'
