INNER JOIN pcm_product_category ppc ON ppc.product_sid = v.spu_sid INNER JOIN pcm_category cate ON cate.sid = ppc.category_sid AND cate.category_type = 0 AND cate.is_leaf = 'Y' AND cate. STATUS = 'Y' AND cate.is_display = 1 LEFT JOIN ( SELECT cate1.category_sid, cate1.`name`, cate1.category_code, ppc1.product_sid FROM pcm_category cate1 JOIN pcm_product_category ppc1 ON cate1.sid = ppc1.category_sid AND cate1.category_type = 2 AND cate1.is_leaf = 'Y' AND cate1. STATUS = 'Y' AND cate1.is_display = 1 ) pc2 ON pc2.product_sid = v.shoppe_pro_sid LEFT JOIN pcm_category cate2 ON cate2.category_code = v.manage_category_sid LEFT JOIN ( SELECT cate3.category_sid, cate3.`name`, cate3.category_code, ppp.product_sid FROM pcm_category cate3 JOIN pcm_product_parameters ppp ON ppp.category_sid = cate3.sid AND cate3.category_type = 3 AND cate3.is_display = 1 AND cate3.is_leaf = 'Y' AND cate3. STATUS = 'Y' ) ppp2 ON ppp2.product_sid = v.spu_sid )PPP3

SELECT ps3.*,ppd.size_picture_url as stockStatus from ( SELECT ps2.product_detail_sid as skuSid,IFNULL(SUM(ps2.pro_sum),0) as saleSum from ( select psp.product_detail_sid, ps.pro_sum from pcm_shoppe_product psp LEFT JOIN pcm_stock ps on psp.shoppe_pro_sid=ps.shoppe_pro_sid
AND ps.channel_sid=#{channelSid,jdbcType=VARCHAR}
AND ps.stock_type_sid=#{stockTypeSid,jdbcType=VARCHAR}
AND ps.stock_type_sid=1001
product_detail_sid=#{skuSid,jdbcType=VARCHAR}
product_detail_sid=(select product_detail_sid from pcm_shoppe_product where shoppe_pro_sid=#{shoppeProSid,jdbcType=VARCHAR} LIMIT 1)
AND psp.sale_status=0
)as ps2 GROUP BY product_detail_sid )ps3 INNER JOIN pcm_pro_detail ppd on ps3.skuSid =ppd.sid

SELECT 1 AS type, pss.shoppe_pro_sid AS matnr, (pss.pro_sum + pss.pro_sum1) AS num, pss.stock_mode AS flag FROM ( SELECT ps.shoppe_pro_sid, ps.pro_sum, IFNULL(ps2.pro_sum, 0) AS pro_sum1, ps.stock_mode FROM ( SELECT ps1.shoppe_pro_sid, ps1.pro_sum, psp.stock_mode FROM pcm_stock ps1 JOIN pcm_shoppe_product psp ON ps1.shoppe_pro_sid = psp.shoppe_pro_sid WHERE ps1.stock_type_sid = '1001' AND ps1.channel_sid = '0'
LIMIT #{page}, 50
) ps LEFT JOIN pcm_stock ps2 ON ps.shoppe_pro_sid = ps2.shoppe_pro_sid AND ps2.stock_type_sid = '1004' AND ps2.stock_type_sid = '0' ) pss

SELECT 1 AS type, pss.shoppe_pro_sid AS matnr, (pss.pro_sum + pss.pro_sum1) AS num, pss.stock_mode AS flag FROM ( SELECT ps.shoppe_pro_sid, ps.pro_sum, IFNULL(ps2.pro_sum, 0) AS pro_sum1, ps.stock_mode FROM ( SELECT ps1.shoppe_pro_sid, ps1.pro_sum, psp.stock_mode FROM pcm_stock ps1 JOIN pcm_shoppe_product psp ON ps1.shoppe_pro_sid = psp.shoppe_pro_sid WHERE ps1.stock_type_sid = '1001'
AND ps1.channel_sid = #{channelSid,jdbcType=VARCHAR}
AND ps1.shoppe_pro_sid IN
#{item}
) ps LEFT JOIN pcm_stock ps2 ON ps.shoppe_pro_sid = ps2.shoppe_pro_sid AND ps2.stock_type_sid = '1004'
AND ps2.channel_sid = #{channelSid,jdbcType=VARCHAR}
) pss

SELECT ps.shoppe_pro_sid, ps.channel_sid, ps.pro_sum FROM pcm_stock ps JOIN ( SELECT field1, COUNT(field1) AS aa FROM pcm_shoppe_product_edi_relation WHERE ifdel = 1 GROUP BY field1
limit #{start}, #{limit}
) psp ON psp.field1 = ps.shoppe_pro_sid AND ps.stock_type_sid = 1001
and ps.channel_sid=#{channelSid,jdbcType=VARCHAR}
